import * as React from 'react-bootstrap/node_modules/react';

import Overlay from 'react-bootstrap/Overlay';

import { PropsOf } from 'react-bootstrap/helpers';

type TriggerType = 'hover' | 'click' | 'focus';

interface OverlayTriggerProps extends PropsOf<typeof Overlay> {
  children: React.ReactNode;
  trigger?: TriggerType | TriggerType[];
  delay?: number | { show: number; hide: number };
  defaultShow?: boolean;
  overlay: React.ReactNode | (() => React.ReactNode);
  target?: never;
  onHide?: never;
  show?: never;
}

declare class OverlayTrigger extends React.Component<OverlayTriggerProps> {}

export default OverlayTrigger;
